import axios from "axios";

const BASE_URL_ENDPOINT = "http://www.omdbapi.com/?apikey=5fb20880";

export  async function getMovies(movieName) {
  const movies = await axios.get(`${BASE_URL_ENDPOINT}&s=${movieName}`);
  return movies;
}

export  async function getMovieDetails(movieID) {
    const movieDetails = await axios.get(`${BASE_URL_ENDPOINT}&i=${movieID}`);
    return movieDetails;
}

export  async function getMovie(movieName) {
  let page=1;
  const moviesList = [];
   while (page <= 5)
     { const res = await axios.get(`${BASE_URL_ENDPOINT}&s=${movieName}&page=${page}`);
       res.forEach(movies => moviesList.push(movies))
       page++;
       res = moviesList;
}
return res;
}
