import axios from "axios";

const BASE_URL_ENDPOINT = "https://www.omdbapi.com/?apikey=5fb20880";

// export  async function getMovies(movieName) {
//   const movies = await axios.get(`${BASE_URL_ENDPOINT}&s=${movieName}`);
//   return movies;
// }

export  async function getMovieDetails(movieID) {
    const movieDetails = await axios.get(`${BASE_URL_ENDPOINT}&i=${movieID}`);
    return movieDetails;
}

export function getTotalPages(movieName) {
  fetch(`${BASE_URL_ENDPOINT}&s=${movieName}&page=1`).then(function(resp){
      return resp.json()
  })
  .then(function(data){
      return data;   
  });
}

export  async function getMovies(movieName) {
  let movie_poster = [];
  let pages = getTotalPages(movieName);
  console.log(pages)
  for(let page=1; page<pages;page++)
  {
  fetch(`${BASE_URL_ENDPOINT}&s=${movieName}&page=${page}`).then(function(resp){
      return resp.json()
  })
  .then(function(data){      
      movie_poster.push(data.Search);
  });
}
  return movie_poster;
}
