import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View, TextInput } from 'react-native';
import React, {useState} from 'react';
import { getMovieDetails } from './apis/api';
import axios from 'axios';

export default function App() {
  const [state, setState] = useState({
    placeholder:"Enter a movie...",
    results:[],
    selected:{}
  });
  const BASE_URL_ENDPOINT = "https://www.omdbapi.com/?apikey=9c538846";


  const fetchMovieDetails = () => {
    axios(BASE_URL_ENDPOINT+"&s="+state.placeholder).then(({data})=>{
      let results =  data.Search;
      setState(prevState => {
        return {...prevState, results:results}
      });
    })

  }

  // async function fetchMovieDetails() {
  //   const { data } = await getMovieDetails(state.placeholder);
  //   let results = data.Search;
  //   console.log(results);ÃŸ
  //   setState(prevState => {
  //     return {...prevState, results:results}
  //   });
  // }
  return (
    <View style={styles.container}>
      <Text style={styles.title}>Movies Inventory</Text>
      <TextInput 
      style={styles.searchBox}
      value= {state.placeholder}
      onChangeText={text=> setState(prevState => {
        return {...prevState, placeholder:text}
      })}
      onSubmitEditing={fetchMovieDetails}
      />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#000000',
    alignItems: 'center',
    justifyContent: 'flex-start',
    paddingTop: 70,
    paddingHorizontal: 20
  },
  title:{
    color: '#FFF',
    fontSize: 24,
    marginBottom:20,
    fontWeight: '500'
  },
  searchBox:{
    width: '50%',
    fontSize: 20,
    padding: 20,
    backgroundColor: '#FFF',
    borderRadius: 8,
    marginBottom: 40
  }
});
