import { StyleSheet, Text, View, TextInput,ScrollView, Image, TouchableHighlight, Modal, TouchableOpacity, Dimensions, StatusBar, ImageBackground, TouchableWithoutFeedback, FlatList} from 'react-native';
import React, {useRef, useState, useEffect} from 'react';
import Carousel from 'react-native-anchor-carousel';
import { FontAwesome5, Feather, MaterialIcons } from '@expo/vector-icons'
import {getMovieDetails, getMovies} from '../apis/api';


export default function Home() {
  const [state, setState] = useState({
    placeholder:"Enter a movie...",
    results:[],
    selected:{}
  });

  async function fetchMovieDetails(paramMovieName) {
    const { data } = await getMovies(paramMovieName);
    let results = data.Search;
    setState(prevState => {
      return {...prevState, results:results}
    });
  }

  async function openModal(id){
    const { data } = await getMovieDetails(id);
    let result = data;
    setState(prevState => {
      return {...prevState, selected:result}
    });
  }

  return (
    <ScrollView>
       <View   style={styles.carouselContentContainer}>
       <View    style={{...StyleSheet.absoluteFill, backgroundColor: '#000'}}>
       <View style={styles.SearchboxContainer}>
       <TextInput
                value={state.placeholder}
                placeholderTextColor='#666'
                style={styles.Searchbox}
                onChangeText={text=> setState(prevState => {
                  return {...prevState, placeholder:text}
                })}
                onSubmitEditing={()=>fetchMovieDetails(state.placeholder)}
                />

              < Feather name='search' size={22} color='#666' style={styles.SearchboxIcon} />
       </View>
         </View>
       </View> 
       <View style={{marginHorizontal: 14}}>
        <ImageBackground
        style={{height: 250, width: '100%', backgroundColor: '000'}}
        resizeMode='cover'
        source={{uri: 'https://www.thehindu.com/entertainment/movies/4xicg2/article26618002.ece/ALTERNATES/LANDSCAPE_1200/how-to-train-your-dragon'
    }}
        >

        <Text style={{color: 'white', padding: 14}}>How to Train Your Dragon: The Hidden World</Text>

          <TouchableOpacity style={{...styles.playIconContainer, position: 'absolute',top: '40%', right: '40%'}}>
            <FontAwesome5  name='play' size={22} color='#02ad94' style={{marginLeft: 4}} />
        </TouchableOpacity>
        </ImageBackground>
        <View style={{flexDirection: 'row', justifyContent: 'space-between',alignItems: 'center',marginBottom: 24, marginTop: 36}}>
        <Text style={{color: 'white', fontSize: 24, fontWeight: 'bold',}}>My List</Text>
        <Text style={{color: '#02ad94', fontSize: 14, fontWeight: 'normal'}}>View All</Text>
        </View>

        <FlatList 
        style={{marginBottom: 30}}
        horizontal={true}
        data={state.results}
        renderItem={({result}) => {
          return(
            <TouchableOpacity style={{marginRight: 20}}>
              <Image source={{uri: result.Poster}} style={{height: 300, width: 200}} />
              <View style={{position: "absolute", height: 5, width: '100%', backgroundColor: '#02ad94',opacity: 0.8}}></View>
              <FontAwesome5  name='play' size={38} color='#fff' style={{position: 'absolute',top: '45%', left: '45%',opacity: 0.9}} />
            </TouchableOpacity>
          )
        }}
        />
    </View>
    </ScrollView>

  );
}

const styles = StyleSheet.create({
  carouselImage: {
    width: 200, 
    height: 320, 
    borderRadius: 10, 
    alignSelf: 'center',
    backgroundColor: 'rgba(0,0,0,0.9)'
},
carouselText: {
    paddingLeft: 14,
    color: 'white', 
    position: 'absolute', 
    bottom: 10, 
    left: 2, 
    fontWeight: 'bold'
},
carouselIcon: {
    position: 'absolute', 
    top: 15, 
    right: 15
},
carouselContentContainer: {
    flex: 1,
    backgroundColor: '#000',
    height: 720,
    paddingHorizontal: 14
  },
SearchboxContainer: {
    flexDirection: 'row',
    marginVertical: 20, 
    width: '95%',
    alignSelf: 'center', 
    backgroundColor: '#fff', 
    elevation: 10,
    borderRadius: 4,
  },
  Searchbox: {
    padding: 12,
    paddingLeft: 20,
    fontSize: 16,
  },
  SearchboxIcon: {
    position: 'absolute', 
    right: 20, 
    top: 14
  },
  ImageBg: {
    flex: 1,
    height: '100%',
    width: '100%',
    opacity: 1,
    justifyContent: 'flex-start',  
  },
  carouselContainerView: {
    width: '100%',
    height:350 ,
    justifyContent: 'center',
    alignItems: 'center',
},
  carousel: {
    flex:1,
    overflow: 'visible',
} ,
movieInfoContainer: {
  flexDirection: 'row', 
  marginTop: 16, 
  justifyContent: 'space-between', 
  width: Dimensions.get('window').width - 14
},
movieName: {
  paddingLeft: 14,
  color: 'white', 
  fontWeight: 'bold', 
  fontSize: 20,
  marginBottom: 6
},
movieStat: {
  paddingLeft: 14,
  color: 'white', 
  fontWeight: 'bold', 
  fontSize: 14, 
  opacity: 0.8
},
playIconContainer: {
  backgroundColor: '#212121',
  padding: 18,
  borderRadius: 40,
  justifyContent: 'center',
  alignItems: 'center',
  elevation: 25,
  borderWidth: 4,
  borderColor: 'rgba(2, 173, 148, 0.2)',
  marginBottom: 14
}
});
