import { StyleSheet, Text, View, TextInput,ScrollView, Image, TouchableHighlight, Modal, TouchableOpacity, Dimensions, StatusBar, ImageBackground, TouchableWithoutFeedback, FlatList} from 'react-native';
import React, {useRef, useState, useEffect} from 'react';
import Carousel from 'react-native-anchor-carousel';
import { FontAwesome5, Feather, MaterialIcons } from '@expo/vector-icons'
import {getMovieDetails, getMovies} from '../apis/api';


export default function Home() {
  const [state, setState] = useState({
    placeholder:"Enter a movie...",
    results:[],
    selected:{}
  });

  const {width, height} = Dimensions.get('window')

  const carouselRef = useRef(null);

  async function fetchMovieDetails(paramMovieName) {
    const { data } = await getMovies(paramMovieName);
    let results = data.Search;
    setState(prevState => {
      return {...prevState, results:results}
    });
  }

  async function openModal(id){
    const { data } = await getMovieDetails(id);
    let result = data;
    setState(prevState => {
      return {...prevState, selected:result}
    });
  }

  const renderItem = () => {
    return (
    <View>
      {state.results.map(result=>  (
          <TouchableOpacity
            onPress={()=>fetchMovieDetails(state.placeholder)}
      >

        <Image source={{uri: result.Poster}} style={styles.carouselImage} />
        <Text style={styles.carouselText}>{result.title}</Text>
        <MaterialIcons name='library-add' size={30} color='white' style={styles.carouselIcon} />    
      </TouchableOpacity>
       ))}

    </View>

    )
  }

  return (
    <ScrollView>
       <View   style={styles.carouselContentContainer}>
       <View    style={{...StyleSheet.absoluteFill, backgroundColor: '#000'}}>
       <ImageBackground source={{ uri: 'https://encrypted-tbn2.gstatic.com/images?q=tbn:ANd9GcQA_-tL18_rj9zEcjN6n41NEaJm-kRNF9UeOtvksZ4z_OW6jRA9'  }} style={styles.ImageBg} blurRadius={10}>
       <View style={styles.SearchboxContainer}>
       <TextInput
                value={state.placeholder}
                placeholderTextColor='#666'
                style={styles.Searchbox}
                onChangeText={text=> setState(prevState => {
                  return {...prevState, placeholder:text}
                })}
                onSubmitEditing={()=>fetchMovieDetails(state.placeholder)}
                />

              < Feather name='search' size={22} color='#666' style={styles.SearchboxIcon} />
       </View>
       <View style={styles.carouselContainerView}>
                <Carousel style={styles.carousel}
                data={state.results}
                renderItem={renderItem}
                itemWidth={200}
                containerWidth={width - 20} 
                separatorWidth={0}
                ref={carouselRef}
                inActiveOpacity={0.4}
            />
      </View>
       </ImageBackground>
         </View>
       </View> 
    </ScrollView>
  );
}

const styles = StyleSheet.create({
  carouselImage: {
    width: 200, 
    height: 320, 
    borderRadius: 10, 
    alignSelf: 'center',
    backgroundColor: 'rgba(0,0,0,0.9)'
},
carouselText: {
    paddingLeft: 14,
    color: 'white', 
    position: 'absolute', 
    bottom: 10, 
    left: 2, 
    fontWeight: 'bold'
},
carouselIcon: {
    position: 'absolute', 
    top: 15, 
    right: 15
},
carouselContentContainer: {
    flex: 1,
    backgroundColor: '#000',
    height: 720,
    paddingHorizontal: 14
  },
SearchboxContainer: {
    flexDirection: 'row',
    marginVertical: 20, 
    width: '95%',
    alignSelf: 'center', 
    backgroundColor: '#fff', 
    elevation: 10,
    borderRadius: 4,
  },
  Searchbox: {
    padding: 12,
    paddingLeft: 20,
    fontSize: 16,
  },
  SearchboxIcon: {
    position: 'absolute', 
    right: 20, 
    top: 14
  },
  ImageBg: {
    flex: 1,
    height: null,
    width: null,
    opacity: 1,
    justifyContent: 'flex-start',  
  },
  carouselContainerView: {
    width: '100%',
    height:350 ,
    justifyContent: 'center',
    alignItems: 'center',
},
  carousel: {
    flex:1,
    overflow: 'visible',
} ,
movieInfoContainer: {
  flexDirection: 'row', 
  marginTop: 16, 
  justifyContent: 'space-between', 
  width: Dimensions.get('window').width - 14
},
movieName: {
  paddingLeft: 14,
  color: 'white', 
  fontWeight: 'bold', 
  fontSize: 20,
  marginBottom: 6
},
movieStat: {
  paddingLeft: 14,
  color: 'white', 
  fontWeight: 'bold', 
  fontSize: 14, 
  opacity: 0.8
},
playIconContainer: {
  backgroundColor: '#212121',
  padding: 18,
  borderRadius: 40,
  justifyContent: 'center',
  alignItems: 'center',
  elevation: 25,
  borderWidth: 4,
  borderColor: 'rgba(2, 173, 148, 0.2)',
  marginBottom: 14
}
});
