import axios from "axios";


const BASE_URL_ENDPOINT = "https://www.omdbapi.com/?apikey=5fb20880";

export  async function getMovieDetails(movieName) {
  const response = await axios.get(`${BASE_URL_ENDPOINT}&s=${movieName}`);
  return response;
}
