import { StyleSheet, Text, View, ImageBackground} from 'react-native';
import React from 'react';

const MovieDetails=({route}) =>{
    const { movie, data } = route.params;


  return (
    <View style={styles.container}>
      <Text key={movie.imdbID}  style={styles.title}>About</Text>
        <View style={styles.modal}>
        <Text style={styles.modalText}>{movie.Title}</Text>
        <ImageBackground
        key={movie.imdbID}
        style={{height: 200, width: '100%', backgroundColor: '000'}}
        resizeMode='contain'
        source={{uri:movie.Poster}}
        ></ImageBackground>
        <Text style={styles.modalText}>Year Released: {movie.Year}</Text>
        {data.Genre!== 'N/A' && (
        <Text style={styles.modalText}>Genre: {data.Genre}</Text>)}
          {data.Actors!== 'N/A' && (
        <Text style={styles.modalText}>Cast: {data.Actors}</Text>)}
        {data.Director!== 'N/A' && (
        <Text style={styles.modalText}>Director: {data.Director}</Text>)}
        {data.Plot!== 'N/A' && (
        <Text style={styles.modalText}>{data.Plot}</Text>)}

         {data.imdbRating!== 'N/A' && (
        <Text style={styles.modalText}>Rating: {data.imdbRating}</Text>)}
        </View>
    </View>
  );
}

export default MovieDetails


const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#000000',
    alignItems: 'center',
    justifyContent: 'flex-start',
    paddingTop: 70,
    paddingHorizontal: 20,
    color: '#fff'
  },
  title:{
    fontSize: 24,
    fontWeight:'700',
    marginBottom: 5,
    color: '#fff',
    alignItems: 'center'
  },

  modal:{
    padding:20,
    marginBottom:20,
    alignItems: 'center'
  },
  modalText:{
    fontSize: 10,
    marginTop:10,
    marginBottom:20,
    fontWeight: '500',
    color: '#fff',
    alignItems: 'center'
  }
});
