import { StyleSheet, Text, View, ImageBackground} from 'react-native';
import React, {useState} from 'react';
import { getMovieDetails } from '../apis/api';

const MovieDetails=({route }) =>{
    const { item } = route.params;
    const [state, setState] = useState('');



        const { data } = getMovieDetails(item.imdbID);
        let result = data;
        setState(result);


  return (
    <View style={styles.container}>
      <Text key={item.imdbID}  style={styles.title}>Movie Description</Text>
        <View style={styles.modal}>
        <Text style={styles.modalText}>{item.Title}</Text>
        <ImageBackground
        style={{height: 300, width: '100%', backgroundColor: '000'}}
        resizeMode='conatin'
        source={item.Poster}
        ></ImageBackground>
        <Text>{state}</Text>
        </View>
    </View>
  );
}

export default MovieDetails


const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#000000',
    alignItems: 'center',
    justifyContent: 'flex-start',
    paddingTop: 70,
    paddingHorizontal: 20,
    color: '#fff'
  },
  title:{
    fontSize: 24,
    fontWeight:'700',
    marginBottom: 5,
    color: '#fff'
  },

  modal:{
    padding:20,
    marginBottom:20
  },
  modalText:{
    fontSize: 24,
    marginBottom:20,
    fontWeight: '500',
    color: '#fff'
  }

});
