  let page =1;
  let results= [];
  const movies = await axios.get(`${BASE_URL_ENDPOINT}&s=${movieName}&page=${page}`);
  let res = Number(movies.data.totalResults);
  console.log(res)
  for(let i=0; i<= res/10; i++)
  {
    console.log(i)
    //const mov = await axios.get(`${BASE_URL_ENDPOINT}&s=${movieName}&page=${i}`);

  }
  return movies;
}

export  async function getMovieDetails(movieID) {
    const movieDetails = await axios.get(`${BASE_URL_ENDPOINT}&i=${movieID}`);
    return movieDetails;
}
