import { StyleSheet, Text, View, TextInput,ScrollView, Image, TouchableHighlight, Modal, TouchableOpacity, Dimensions, ImageBackground, FlatList} from 'react-native';
import React, {useState} from 'react';
import {  Feather } from '@expo/vector-icons'
import {getMovieDetails, getMovies} from '../apis/api';
import APP_BANNER from '../assets/images/istockphoto-1191001701-612x612.jpeg';
import ErrorScreen from './error-screen';
import { useNavigation } from '@react-navigation/native';

export default function Home() {
  const [state, setState] = useState({
    placeholder:"Enter a movie",
    results:[],
    selected:{}
  });

  async function fetchMovieDetails(paramMovieName) {
    const { data } = await getMovies(paramMovieName);
    let results = data.Search;
    setState(prevState => {
      return {...prevState, results:results}
    });
  }

  async function openModal(id){
    const { data } = await getMovieDetails(id);
    let result = data;
    setState(prevState => {
      return {...prevState, selected:result}
    });
  }

  return (
    <ScrollView style={{backgroundColor: '#000'}} blurRadius={100}>
       <View  style={{...StyleSheet.absoluteFill, backgroundColor: '#000'}}>
       </View> 
       <View style={{marginHorizontal: 14}}>
        <ImageBackground
        style={{height: 350, width: '100%', backgroundColor: '000'}}
        resizeMode='cover'
        source={APP_BANNER}
        >
          <View style={styles.SearchboxContainer}>
             <TextInput
                value={state.placeholder}
                placeholderTextColor='#666'
                style={styles.Searchbox}
                onChangeText={text=> setState(prevState => {
                  return {...prevState, placeholder:text}
                })}
                onSubmitEditing={()=>fetchMovieDetails(state.placeholder)}
                onFocus= {() => setState(prevState => {
                  return {...prevState, placeholder:''}
                })}
                onBlur={() => setState(prevState => {
                  return {...prevState, placeholder: state.placeholder}
                })}
                />
              < Feather name='search' size={22} color='#666' style={styles.SearchboxIcon} /> 
              </View>
        </ImageBackground> 
        {state.results ? (
        <FlatList 
        style={{marginBottom: 30}}
        horizontal={true}
        data={state.results}
        renderItem={( {item} ) =>  (
           <TouchableHighlight  key={item.imdbID} onPress={()=>openModal(item.imdbID)} >
            <TouchableOpacity style={{marginRight: 20}}>
              <Image source={{uri: item.Poster}} style={{height: 300, width: 200}} />
              <View style={{position: "absolute", height: 5, width: '100%',opacity: 0.8}}></View>
            </TouchableOpacity>
            </TouchableHighlight> 
          )}
        />
        ) : (<ErrorScreen/>)}
    </View>
    <Modal 
      animationType='fade'
      transparent={false}
      visible={(typeof state.selected.Title!="undefined")? true: false}
      >
        <View style={styles.modal}>
        <Text style={styles.modalText}>{state.selected.Title}</Text>
        <Text style={{marginBottom:20}}>Rating: {state.selected.imdbRating}</Text>
        <Text>{state.selected.Plot}</Text>
        </View>
        <TouchableHighlight
        onPress={()=>setState(prevState => {
          return {...prevState, selected:{}}
        })}
        >
        <Text style={styles.closeButton}>Close</Text>
        </TouchableHighlight>
      </Modal>
    </ScrollView>


  );
}

const styles = StyleSheet.create({
  carouselImage: {
    width: 200, 
    height: 320, 
    borderRadius: 10, 
    alignSelf: 'center',
    backgroundColor: 'rgba(0,0,0,0.9)'
},
carouselText: {
    paddingLeft: 14,
    color: 'white', 
    position: 'absolute', 
    bottom: 10, 
    left: 2, 
    fontWeight: 'bold'
},
carouselIcon: {
    position: 'absolute', 
    top: 15, 
    right: 15
},
carouselContentContainer: {
    flex: 1,
    backgroundColor: '#000',
    height: 720,
    paddingHorizontal: 14
  },
SearchboxContainer: {
    flexDirection: 'row',
    marginVertical: 20, 
    width: '50%',
    alignSelf: 'center', 
    backgroundColor: '#fff', 
    elevation: 10,
    borderRadius: 4,
  },
  Searchbox: {
    padding: 12,
    paddingLeft: 20,
    fontSize: 16,
    width: '100%'
  },
  SearchboxIcon: {
    position: 'absolute', 
    right: 20, 
    top: 14
  },
  ImageBg: {
    flex: 1,
    height: '100%',
    width: '100%',
    opacity: 1,
    justifyContent: 'flex-start',  
  },
  carouselContainerView: {
    width: '100%',
    height:350 ,
    justifyContent: 'center',
    alignItems: 'center',
},
  carousel: {
    flex:1,
    overflow: 'visible',
} ,
movieInfoContainer: {
  flexDirection: 'row', 
  marginTop: 16, 
  justifyContent: 'space-between', 
  width: Dimensions.get('window').width - 14
},
movieName: {
  paddingLeft: 14,
  color: 'white', 
  fontWeight: 'bold', 
  fontSize: 20,
  marginBottom: 6
},
movieStat: {
  paddingLeft: 14,
  color: 'white', 
  fontWeight: 'bold', 
  fontSize: 14, 
  opacity: 0.8
},
});
