import { StyleSheet, Text, View, ImageBackground} from 'react-native';
import React from 'react';

const MovieDetails=({route }) =>{
    const { item } = route.params;
  return (
    <View style={styles.container}>
      <Text key={item.imdbID}  style={styles.title}>Movie Description</Text>
        <View style={styles.modal}>
        <Text style={styles.modalText}>{item.Title}</Text>
        <ImageBackground
        style={{height: 250, width: '200%', backgroundColor: '000'}}
        resizeMode='cover'
        source={item.Poster}
        ></ImageBackground>
        <Text style={{marginBottom:20,fontSize: 24, fontWeight:'700',marginBottom: 5, color: '#fff'}}>Rating: {item.imdbRating}</Text>
        <Text>{item.Plot}</Text>
        </View>
    </View>
  );
}

export default MovieDetails


const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#000000',
    alignItems: 'center',
    justifyContent: 'flex-start',
    paddingTop: 70,
    paddingHorizontal: 20,
    color: '#fff'
  },
  title:{
    fontSize: 24,
    marginBottom:20,
    fontWeight: '500',
    color: '#fff'
  },

  modal:{
    padding:20,
    marginBottom:20
  },
  modalText:{
    fontSize: 24,
    fontWeight:'700',
    marginBottom: 5,
    color: '#fff'
  }

});
