import { StyleSheet, Text, View, TextInput,ScrollView, Image, TouchableHighlight, Modal, TouchableOpacity, Dimensions, StatusBar, ImageBackground, TouchableWithoutFeedback, FlatList} from 'react-native';
import React, {useRef, useState, useEffect} from 'react';
import Carousel from 'react-native-anchor-carousel';
import { FontAwesome5, Feather, MaterialIcons } from '@expo/vector-icons'
import {getMovieDetails, getMovies} from '../apis/api';

export default function MovieDetails() {
  const [state, setState] = useState({
    placeholder:"Enter a movie...",
    results:[],
    selected:{}
  });

  async function openModal(id){
    const { data } = await getMovieDetails(id);
    let result = data;
    setState(prevState => {
      return {...prevState, selected:result}
    });
  }
  return (
    <View style={styles.container}>
      <Text style={styles.title}>Movies Inventory</Text>
        <ScrollView style={styles.scrollGrids}>
        {state.results.map(result=>  (
          <TouchableHighlight key={result.imdbID}  
           onPress={()=>openModal(result.imdbID)}>
          <View style={styles.scrollGrid}>
            <Image 
             source={{uri: ''}}
             style={{
               width:'100%',
               marginHorizontal:'auto',
               height:300,
             }}
             resizeMode='cover'
             />
            <Text style={styles.heading}>{result.Title}</Text>
          </View>
          </TouchableHighlight>    
          )
        )}
      </ScrollView>
      <Modal 
      animationType='fade'
      transparent={false}
      visible={(typeof state.selected.Title!="undefined")? true: false}
      >
        <View style={styles.modal}>
        <Text style={styles.modalText}>{state.selected.Title}</Text>
        <Text style={{marginBottom:20}}>Rating: {state.selected.imdbRating}</Text>
        <Text>{state.selected.Plot}</Text>
        </View>
        <TouchableHighlight
        onPress={()=>setState(prevState => {
          return {...prevState, selected:{}}
        })}
        >
        <Text style={styles.closeButton}>Close</Text>
        </TouchableHighlight>
      </Modal>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#000000',
    alignItems: 'center',
    justifyContent: 'flex-start',
    paddingTop: 70,
    paddingHorizontal: 20
  },
  title:{
    color: '#FFF',
    fontSize: 24,
    marginBottom:20,
    fontWeight: '500'
  },
  searchBox:{
    width: '50%',
    fontSize: 20,
    padding: 20,
    backgroundColor: '#FFF',
    borderRadius: 8,
    marginBottom: 40
  },
  scrollGrids:{
    flex: 1,
  },
  scrollGrid:{
    flex: 1,
    width:"100%",
    marginBottom:20
  },
  heading:{
    color: '#FFF',
    fontSize: 18,
    fontWeight:'700',
    padding: 20,
    backgroundColor: '#445565'
  },
  modal:{
    padding:20,
    marginBottom:20
  },
  modalText:{
    fontSize: 24,
    fontWeight:'700',
    marginBottom: 5
  },
  closeButton:{
    padding:20,
    fontSize:20,
    fontWeight:'700',
    backgroundColor:'#2484C4',
    color: '#FFF'    
  }

});
