import { StyleSheet, Text, View, TextInput,ScrollView, Image, TouchableHighlight, Modal, TouchableOpacity, Dimensions, StatusBar, ImageBackground, TouchableWithoutFeedback, FlatList} from 'react-native';
import React, {useRef, useState, useEffect} from 'react';
import Carousel from 'react-native-anchor-carousel';
import { FontAwesome5, Feather, MaterialIcons } from '@expo/vector-icons'
import {getMovieDetails, getMovies} from '../apis/api';


export default function Home() {
  const [state, setState] = useState({
    placeholder:"Enter a movie...",
    results:[],
    selected:{}
  });

  async function fetchMovieDetails(paramMovieName) {
    const { data } = await getMovies(paramMovieName);
    let results = data.Search;
    setState(prevState => {
      return {...prevState, results:results}
    });
  }

  async function openModal(id){
    const { data } = await getMovieDetails(id);
    let result = data;
    setState(prevState => {
      return {...prevState, selected:result}
    });
  }
  return (
    <ScrollView>

        </ScrollView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#000000',
    alignItems: 'center',
    justifyContent: 'flex-start',
    paddingTop: 70,
    paddingHorizontal: 20
  },
  title:{
    color: '#FFF',
    fontSize: 24,
    marginBottom:20,
    fontWeight: '500'
  },
  searchBox:{
    width: '50%',
    fontSize: 20,
    padding: 20,
    backgroundColor: '#FFF',
    borderRadius: 8,
    marginBottom: 40
  },
  scrollGrids:{
    flex: 1,
  },
  scrollGrid:{
    flex: 1,
    width:"100%",
    marginBottom:20
  },
  heading:{
    color: '#FFF',
    fontSize: 18,
    fontWeight:'700',
    padding: 20,
    backgroundColor: '#445565'
  },
  modal:{
    padding:20,
    marginBottom:20
  },
  modalText:{
    fontSize: 24,
    fontWeight:'700',
    marginBottom: 5
  },
  closeButton:{
    padding:20,
    fontSize:20,
    fontWeight:'700',
    backgroundColor:'#2484C4',
    color: '#FFF'    
  }

});
