import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View, TextInput,ScrollView, Image, TouchableHighlight } from 'react-native';
import React, {useState} from 'react';
import { getMovieDetails } from './apis/api';
import axios from 'axios';

export default function App() {
  const [state, setState] = useState({
    placeholder:"Enter a movie...",
    results:[],
    selected:{}
  });

  async function fetchMovieDetails() {
    const { data } = await getMovieDetails(state.placeholder);
    let results = data.Search;
    console.log(results);
    setState(prevState => {
      return {...prevState, results:results}
    });
  }
  return (
    <View style={styles.container}>
      <Text style={styles.title}>Movies Inventory</Text>
      <TextInput 
      style={styles.searchBox}
      value= {state.placeholder}
      onChangeText={text=> setState(prevState => {
        return {...prevState, placeholder:text}
      })}
      onSubmitEditing={fetchMovieDetails}
      />
        <ScrollView style={styles.scrollGrids}>
        {state.results.map(result=>  (
          <TouchableHighlight onPress={()=>openModal(result.imdbID)}>
          <View key={result.imdbID} style={styles.scrollGrid}>
            <Image 
             source={{uri: result.Poster}}
             style={{
               width:'100%',
               marginHorizontal:'auto',
               height:300,
             }}
             resizeMode='cover'
             />
            <Text style={styles.heading}>{result.Title}</Text>
          </View>
          </TouchableHighlight>    
          )
        )}
      </ScrollView>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#000000',
    alignItems: 'center',
    justifyContent: 'flex-start',
    paddingTop: 70,
    paddingHorizontal: 20
  },
  title:{
    color: '#FFF',
    fontSize: 24,
    marginBottom:20,
    fontWeight: '500'
  },
  searchBox:{
    width: '50%',
    fontSize: 20,
    padding: 20,
    backgroundColor: '#FFF',
    borderRadius: 8,
    marginBottom: 40
  },
  scrollGrids:{
    flex: 1,
  },
  scrollGrid:{
    flex: 1,
    width:"100%",
    marginBottom:20
  },
  heading:{
    color: '#FFF',
    fontSize: 18,
    fontWeight:700,
    padding: 20,
    backgroundColor: '#445565'
  }

});
